export default function sketch(p5){

let angle = 0;
let w = 24;


    p5.setup = () => {
      p5.createCanvas(400, 400, p5.WEBGL);
    }

    p5.draw = () => {
      p5.background(175);
      p5.translate(0, 50, -50);
      p5.rotate(0, PI / 8);
      //p5.translate(p5.width / 2, p5.height / 2)
      p5.rectMode(p5.CENTER);

      //p5.rotateX(angle * 0.25);

      let offset = 0;
      for (let x = 0; x < p5.width; x+= w) {
        p5.push();
        let a = angle + offset;
        let h = p5.map(p5.sin(a), -1, 1, 0, 100);
        p5.fill(255);
        p5.translate(x - p5.width / 2, 0, 0);
        p5.box(w, h, w);
        //p5.rect(x - p5.width / 2 + w / 2, 0, w - 2, h);
        offset += 0.1;
        p5.pop();
      }

      angle += 0.1;
    };

};
