export default function sketch(p5){

let angle = 0;

    p5.setup = () => {
      p5.createCanvas(400, 400);
    }

    p5.draw = () => {
      p5.background(220,0,200);
      p5.translate(p5.width / 2, p5.height / 2)
      p5.rectMode(p5.CENTER);

      for (let x= 0; x < width; x+= 10) {
        let h = p5.map(p5.sin(angle), -1, 1, 0, 100);
        p5.fill(255);
        p5.rect(0, 0, 10, h);

      }

      angle += 0.1;
    };

};
